mapper.select:
SELECT
intSeq, intSSeq, intMSeq, intType, strURL, strFileName, intSort, dateReg
FROM
TB_STORE_FILE
WHERE
intSeq > 0

    AND intSSeq = #{search.intSSeq}


    AND intMSeq = #{search.intMSeq}


    AND intType = #{search.intType}


    AND intSeq = #{search.intSeq}

ORDER BY
    intSort, intSeq DESC

    LIMIT #{limit}, #{length}

mapper.delete:
DELETE FROM TB_STORE_FILE
WHERE

    AND intSSeq = #{intSSeq}


    AND intMSeq = #{intMSeq}


    AND intType = #{intType}


    AND intSeq = #{intSeq}
